            dataKey="trend"
            stroke="#ea580c"
            strokeWidth={2}
            strokeDasharray="5 5"
            dot={false}
            name="Trend"
          />

          {/* Bollinger Bands */}
          {showIndicators.bollingerBands && (
            <>
              <Line
                type="monotone"
                dataKey="bbUpper"
                stroke="#f59e0b"
                strokeWidth={1}
                dot={false}
