                    initialPrice={stockData.currentPrice}
                    isDarkMode={false}
                  />
                )}
                {activeTab === 'distribution' && (
                  <DistributionChart
                    histogram={analyticsResult.histogram}
                    initialPrice={stockData.currentPrice}
                    isDarkMode={false}
                  />
