import React, { useState, useEffect, useRef, useCallback } from 'react';
import './App.css';
import { DataFetcher } from './DataFetch';
import { Analytics } from './Analytics';
import { StockData, AnalyticsResult, AnalyticsParams } from './types';
import { HistoricalChart, VolatilityChart, MonteCarloPathsChart, DistributionChart, PerformanceMetricsChart } from './Charts';

function App() {
  const [stockData, setStockData] = useState<StockData | null>(null);
  const [analyticsResult, setAnalyticsResult] = useState<AnalyticsResult | null>(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [ticker, setTicker] = useState('AAPL');
  const [activeTab, setActiveTab] = useState('historical');
  const [isDarkMode, setIsDarkMode] = useState(false);
  const [simulationParams, setSimulationParams] = useState<AnalyticsParams>({
    alpha: 0.1,
    beta: 0.8,
    theta: 0.05,
    switchProb: 0.05,
    numPaths: 1000,
    numSteps: 252
  });

  const workerRef = useRef<Worker | null>(null);
  const dataFetcherRef = useRef<DataFetcher>(new DataFetcher());

  // Toggle dark mode
  const toggleDarkMode = () => {
    setIsDarkMode(!isDarkMode);
    document.documentElement.classList.toggle('dark');
  };

  // Fetch stock data
  const fetchData = useCallback(async (symbol: string) => {
    setLoading(true);
    setError(null);
    try {
      const data = await dataFetcherRef.current.fetchStockData(symbol);
      setStockData(data);
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Failed to fetch data');
    } finally {
      setLoading(false);
    }
  }, []);

  // Run Monte Carlo simulation
  const runSimulation = useCallback(async () => {
    if (!stockData) return;

    setLoading(true);
    setError(null);

    try {
      // Create Web Worker for Monte Carlo simulation
      const workerCode = `
        ${Analytics.runMonteCarloSimulation.toString()}
        ${Analytics.calculateGARCHVolatility.toString()}
        ${Analytics.calculateTrendAndDrift.toString()}
        ${Analytics.generateMarkovRegime.toString()}

        self.onmessage = function(e) {
          try {
            const returns = Analytics.calculateReturns(e.data.stockData.prices);
            const volatilities = Analytics.calculateGARCHVolatility(returns, e.data.params.alpha, e.data.params.beta);
            const { trend, drift } = Analytics.calculateTrendAndDrift(e.data.stockData.prices, e.data.params.theta);
            const regimes = Analytics.generateMarkovRegime(returns, e.data.params.switchProb);

            const result = Analytics.runMonteCarloSimulation(
              e.data.stockData.currentPrice,
              e.data.params,
              volatilities[volatilities.length - 1],
              drift
            );

            // Add additional data to result
            result.currentVolatility = volatilities[volatilities.length - 1];
            result.trend = trend;
            result.drift = drift;

            self.postMessage({ success: true, result });
          } catch (error) {
            self.postMessage({ success: false, error: error.message });
          }
        };
      `;

      const blob = new Blob([workerCode], { type: 'application/javascript' });
      workerRef.current = new Worker(URL.createObjectURL(blob));

      workerRef.current.onmessage = (e) => {
        if (e.data.success) {
          setAnalyticsResult(e.data.result);
        } else {
          setError(e.data.error);
        }
        setLoading(false);
      };

      workerRef.current.onerror = (e) => {
        setError('Simulation failed: ' + e.message);
        setLoading(false);
      };

      workerRef.current.postMessage({
        stockData,
        params: simulationParams
      });

    } catch (err) {
      setError(err instanceof Error ? err.message : 'Simulation failed');
      setLoading(false);
    }
  }, [stockData, simulationParams]);

  // Cleanup worker on unmount
  useEffect(() => {
    return () => {
      if (workerRef.current) {
        workerRef.current.terminate();
      }
    };
  }, []);

  // Fetch data on component mount
  useEffect(() => {
    fetchData(ticker);
  }, [fetchData, ticker]);

  // Run simulation when stock data changes
  useEffect(() => {
    if (stockData) {
      runSimulation();
    }
  }, [stockData, runSimulation]);

  const handleTickerChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    setTicker(e.target.value.toUpperCase());
  };

  const handleTickerSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    fetchData(ticker);
  };

  const updateSimulationParam = (param: keyof AnalyticsParams, value: number) => {
    setSimulationParams((prev: AnalyticsParams) => ({
      ...prev,
      [param]: value
    }));
  };

  return (
    <div className={`min-h-screen ${isDarkMode ? 'dark bg-gray-900' : 'bg-gray-50'}`}>
      {/* Header */}
      <header className="bg-white dark:bg-gray-800 shadow-sm border-b border-gray-200 dark:border-gray-700">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center h-16">
            <div className="flex items-center">
              <h1 className="text-xl font-bold text-gray-900 dark:text-white">
                Finance Analytics Widget
              </h1>
            </div>

            <div className="flex items-center space-x-4">
              {/* Ticker Input */}
              <form onSubmit={handleTickerSubmit} className="flex items-center">
                <input
                  type="text"
                  value={ticker}
                  onChange={handleTickerChange}
                  placeholder="Enter ticker..."
                  className="px-3 py-2 border border-gray-300 dark:border-gray-600 rounded-l-md focus:outline-none focus:ring-2 focus:ring-blue-500 dark:bg-gray-700 dark:text-white"
                />
                <button
                  type="submit"
                  disabled={loading}
                  className="px-4 py-2 bg-blue-600 text-white rounded-r-md hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-500 disabled:opacity-50"
                >
                  {loading ? 'Loading...' : 'Analyze'}
                </button>
              </form>

              {/* Dark Mode Toggle */}
              <button
                onClick={toggleDarkMode}
                className="p-2 rounded-md bg-gray-200 dark:bg-gray-700 hover:bg-gray-300 dark:hover:bg-gray-600"
              >
                {isDarkMode ? '‚òÄÔ∏è' : 'üåô'}
              </button>
            </div>
          </div>
        </div>
      </header>

      {/* Main Content */}
      <main className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {error && (
          <div className="mb-6 p-4 bg-red-50 dark:bg-red-900/20 border border-red-200 dark:border-red-800 rounded-md">
            <p className="text-red-800 dark:text-red-200">{error}</p>
          </div>
        )}

        {stockData && (
          <>
            {/* Stock Info */}
            <div className="mb-6 bg-white dark:bg-gray-800 rounded-lg shadow p-6">
              <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
                <div>
                  <h2 className="text-2xl font-bold text-gray-900 dark:text-white">
                    {stockData.ticker}
                  </h2>
                  <p className="text-3xl font-bold text-green-600 dark:text-green-400">
                    ${stockData.currentPrice.toFixed(2)}
                  </p>
                </div>

                <div className="space-y-2">
                  <div className="flex justify-between">
                    <span className="text-gray-600 dark:text-gray-400">P/E Ratio:</span>
                    <span className="text-gray-900 dark:text-white">
                      {stockData.fundamentals.pe?.toFixed(2) || 'N/A'}
                    </span>
                  </div>
                  <div className="flex justify-between">
                    <span className="text-gray-600 dark:text-gray-400">Market Cap:</span>
                    <span className="text-gray-900 dark:text-white">
                      ${(stockData.fundamentals.marketCap ? stockData.fundamentals.marketCap / 1e9 : 0).toFixed(2)}B
                    </span>
                  </div>
                  <div className="flex justify-between">
                    <span className="text-gray-600 dark:text-gray-400">Dividend Yield:</span>
                    <span className="text-gray-900 dark:text-white">
                      {stockData.fundamentals.dividendYield?.toFixed(2) || 'N/A'}%
                    </span>
                  </div>
                </div>

                <div className="space-y-2">
                  <div className="flex justify-between">
                    <span className="text-gray-600 dark:text-gray-400">P/B Ratio:</span>
                    <span className="text-gray-900 dark:text-white">
                      {stockData.fundamentals.pb?.toFixed(2) || 'N/A'}
                    </span>
                  </div>
                  <div className="flex justify-between">
                    <span className="text-gray-600 dark:text-gray-400">PEG Ratio:</span>
                    <span className="text-gray-900 dark:text-white">
                      {stockData.fundamentals.peg?.toFixed(2) || 'N/A'}
                    </span>
                  </div>
                  <div className="flex justify-between">
                    <span className="text-gray-600 dark:text-gray-400">EPS:</span>
                    <span className="text-gray-900 dark:text-white">
                      {stockData.fundamentals.eps?.toFixed(2) || 'N/A'}
                    </span>
                  </div>
                </div>
              </div>
            </div>

            {/* Simulation Parameters */}
            <div className="mb-6 bg-white dark:bg-gray-800 rounded-lg shadow p-6">
              <h3 className="text-lg font-semibold text-gray-900 dark:text-white mb-4">
                Simulation Parameters
              </h3>
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                    Alpha (Œ±): {simulationParams.alpha}
                  </label>
                  <input
                    type="range"
                    min="0.01"
                    max="0.3"
                    step="0.01"
                    value={simulationParams.alpha}
                    onChange={(e) => updateSimulationParam('alpha', parseFloat(e.target.value))}
                    className="w-full"
                  />
                  <p className="text-xs text-gray-500 dark:text-gray-400 mt-1">
                    GARCH volatility persistence
                  </p>
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                    Beta (Œ≤): {simulationParams.beta}
                  </label>
                  <input
                    type="range"
                    min="0.5"
                    max="0.95"
                    step="0.01"
                    value={simulationParams.beta}
                    onChange={(e) => updateSimulationParam('beta', parseFloat(e.target.value))}
                    className="w-full"
                  />
                  <p className="text-xs text-gray-500 dark:text-gray-400 mt-1">
                    GARCH volatility clustering
                  </p>
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                    Theta (Œ∏): {simulationParams.theta}
                  </label>
                  <input
                    type="range"
                    min="0.01"
                    max="0.2"
                    step="0.01"
                    value={simulationParams.theta}
                    onChange={(e) => updateSimulationParam('theta', parseFloat(e.target.value))}
                    className="w-full"
                  />
                  <p className="text-xs text-gray-500 dark:text-gray-400 mt-1">
                    Mean reversion strength
                  </p>
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 dark:text-gray-300 mb-2">
                    Regime Switch: {(simulationParams.switchProb * 100).toFixed(1)}%
                  </label>
                  <input
                    type="range"
                    min="0.01"
                    max="0.2"
                    step="0.01"
                    value={simulationParams.switchProb}
                    onChange={(e) => updateSimulationParam('switchProb', parseFloat(e.target.value))}
                    className="w-full"
                  />
                  <p className="text-xs text-gray-500 dark:text-gray-400 mt-1">
                    Market regime change probability
                  </p>
                </div>
              </div>
            </div>

            {/* Navigation Tabs */}
            <div className="mb-6">
              <nav className="flex space-x-8">
                {[
                  { id: 'historical', label: 'Historical Data' },
                  { id: 'volatility', label: 'Volatility Analysis' },
                  { id: 'monte-carlo', label: 'Monte Carlo Simulation' },
                  { id: 'distribution', label: 'Price Distribution' },
                  { id: 'metrics', label: 'Risk Metrics' }
                ].map((tab) => (
                  <button
                    key={tab.id}
                    onClick={() => setActiveTab(tab.id)}
                    className={`py-2 px-1 border-b-2 font-medium text-sm ${
                      activeTab === tab.id
                        ? 'border-blue-500 text-blue-600 dark:text-blue-400'
                        : 'border-transparent text-gray-500 hover:text-gray-700 hover:border-gray-300 dark:text-gray-400 dark:hover:text-gray-300'
                    }`}
                  >
                    {tab.label}
                  </button>
                ))}
              </nav>
            </div>

            {/* Charts */}
            {analyticsResult && (
              <div className="bg-white dark:bg-gray-800 rounded-lg shadow p-6">
                {activeTab === 'historical' && (
                  <HistoricalChart
                    prices={stockData.prices}
                    trend={analyticsResult.trend}
                    isDarkMode={isDarkMode}
                  />
                )}
                {activeTab === 'volatility' && (
                  <VolatilityChart
                    prices={stockData.prices}
                    volatilities={[]} // TODO: Calculate volatilities
                    regimes={[]} // TODO: Calculate regimes
                    isDarkMode={isDarkMode}
                  />
                )}
                {activeTab === 'monte-carlo' && (
                  <MonteCarloPathsChart
                    paths={analyticsResult.paths}
                    initialPrice={stockData.currentPrice}
                    isDarkMode={isDarkMode}
                  />
                )}
                {activeTab === 'distribution' && (
                  <DistributionChart
                    histogram={analyticsResult.histogram}
                    initialPrice={stockData.currentPrice}
                    isDarkMode={isDarkMode}
                  />
                )}
                {activeTab === 'metrics' && (
                  <div className="space-y-4">
                    <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
                      <div className="bg-gray-50 dark:bg-gray-700 p-4 rounded-lg">
                        <h4 className="text-sm font-medium text-gray-600 dark:text-gray-400">VaR (95%)</h4>
                        <p className="text-lg font-bold text-red-600 dark:text-red-400">
                          ${analyticsResult.var95.toFixed(2)}
                        </p>
                      </div>
                      <div className="bg-gray-50 dark:bg-gray-700 p-4 rounded-lg">
                        <h4 className="text-sm font-medium text-gray-600 dark:text-gray-400">VaR (99%)</h4>
                        <p className="text-lg font-bold text-red-600 dark:text-red-400">
                          ${analyticsResult.var99.toFixed(2)}
                        </p>
                      </div>
                      <div className="bg-gray-50 dark:bg-gray-700 p-4 rounded-lg">
                        <h4 className="text-sm font-medium text-gray-600 dark:text-gray-400">Upside 20%</h4>
                        <p className="text-lg font-bold text-green-600 dark:text-green-400">
                          {(analyticsResult.probabilities.upside20 * 100).toFixed(1)}%
                        </p>
                      </div>
                      <div className="bg-gray-50 dark:bg-gray-700 p-4 rounded-lg">
                        <h4 className="text-sm font-medium text-gray-600 dark:text-gray-400">Downside 10%</h4>
                        <p className="text-lg font-bold text-red-600 dark:text-red-400">
                          {(analyticsResult.probabilities.downside10 * 100).toFixed(1)}%
                        </p>
                      </div>
                    </div>
                  </div>
                )}
              </div>
            )}

            {loading && (
              <div className="flex justify-center items-center py-12">
                <div className="loading-spinner"></div>
                <span className="ml-3 text-gray-600 dark:text-gray-400">
                  Running Monte Carlo simulation...
                </span>
              </div>
            )}
          </>
        )}

        {!stockData && !loading && (
          <div className="text-center py-12">
            <p className="text-gray-600 dark:text-gray-400">
              Enter a stock ticker to begin analysis
            </p>
          </div>
        )}
      </main>
    </div>
  );
}

export default App;
